import React, { useState, useEffect, useRef} from 'react';
// import App from '../App';
// import TodoEdit from './TodoEdit';
// import TodoDelete from './TodoDelete';

function TodoApp (){
    const [job,setJob] = useState([]);
  // danh sach cong viec(neu local ko luu thi tra ve mang rong)
  // toan tu nulllist
  const [jobs,setJobs] = useState([]);
  const inputRef = useRef();
  const [indexOfJobEdit, setIndexOfJobEdit] = useState(null);
  const handleSubmit = () => {
    // lấy ra mang setJobs 
    //để bảo lưu những job trong tương lai khi mà thêm job mới
    // console.log(indexOfJobEdit)
      setJobs( prve => { 
      const newJobs = [...prve,job];
      localStorage.setItem('jobs', JSON.stringify(newJobs));
      return newJobs;
      })
      // prev bảo lưu, job là cv mới
      // sau khi setjob thì lại trả về mảng rỗng cho input
      setJob('')
    }
    const handleOnKeyDown = (event) => {
        // console.log(event)
        // console.log(indexOfJobEdit,1)
        if (event.key === 'Enter') {
          handleSubmit();
        }
    }
    const handleOnFocus = (job, index) => {
              // handlEditJob()
              setIndexOfJobEdit(index)
              setJob(job)
              // console.log(indexOfJobEdit, 2)
     }

    useEffect(() => {
        const jobs = localStorage.getItem('jobs') || "[]";

        try {
          const parsedJobs = JSON.parse(jobs);
          // Array.isArray ham xac dinh kieu cuar bien co phai mang hay ko
          // ham .length de xac dinh so luong phan tu trong mang
          if (!Array.isArray(parsedJobs) || parsedJobs.length === 0) {
            return;
          }
          setJobs(parsedJobs)
        } catch (error) {
          console.log('error', error);
        }
      }, [])
      return (
        <form className="TodoApp" >
          <input
            // ref={input1} 
            type="text" 
            name="Add" 
            placeholder="Add"
            onKeyDown={handleOnKeyDown}
            value={job}
            // event goi setJob
            onChange={e => setJob(e.target.value)}
          />
            <button onClick={handleSubmit}>ADD</button>

        </form>
  );
}

export default TodoApp;
