import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [job, setJob] = useState('')
  // danh sach cong viec
  const [jobs, setJobs] = useState([])

  return (
    <div className="App">
      {/* sd two-way binding */}
      <input 
        value={job} 
        // event goi setJob
        onChange = {e => setJob(e.target.value)}
      />
      <button>ADD</button>
      <ul>
        {/* render ra the li*/}
        {jobs.map((job, index) =>(
          // render job lamf children cua li
          <li key={index}>{job}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
