// import logo from './logo.svg';
import './App.css';
import {  useEffect, useState } from 'react';

// thêm công việc 
function App() {
  const [job, setJob] = useState([]);
  // danh sach cong viec(neu local ko luu thi tra ve mang rong)
  // toan tu nulllist
  const [jobs, setJobs] = useState([]);
  // gui du lieu cua form di
  const handleSubmit =() => {

    // lấy ra mang setJobs 
    //để bảo lưu những job trong tương lai khi mà thêm job mới
    setJobs(prev =>{
      // tạo newJobs để lưu job vào local storage
      const newJobs = [...prev,job]
      // tạo 1 chuỗi json: save to localstorage
      const jsonJobs = JSON.stringify(newJobs)
      // lấy chuỗi json lưu vào local storage

      return newJobs
    })
    localStorage.setItem('jobs', jsonJobs)
    // prev bảo lưu, job là cv mới
    // sau khi setjob thì lại trả về mảng rỗng cho input
    setJob('')


  }

  const handleDeleteJob = (index) => {
      let newJobs = [...jobs];
      newJobs = newJobs.filter(job => {
        return job !==index
      });
      setJobs(newJobs);
    };


  const handleOnKeyDown = (event) => {
    console.log(event)
    if(event.key === 'Enter') {
      handleSubmit();
    }
  }
// 
  useEffect(() => {
    const jobs = localStorage.getItem('jobs') || "[]";

    try {
      const parsedJobs = JSON.parse(jobs);

      if (!Array.isArray(parsedJobs) || parsedJobs.length === 0) {
        return;
      }
      setJobs(parsedJobs)
    } catch (error) {
      console.log('error', error);
    }
  }, [])

  return (
    <div className="App" >
      {/* sd two-way binding */}
      <input 
        onKeyDown={handleOnKeyDown}
        value={job} 
        // event goi setJob
        onChange = {e => setJob(e.target.value)}
      />
      <button onClick={handleSubmit}>ADD</button>
      <ul>
        {/* render ra the li*/}
        {jobs.map((job, index) =>(
          // render job lamf children cua li
          <li key={index}>
            <span>
              {job}
            </span>
          <button onClick={() => handleDeleteJob(job)}>Delete</button>
          <button>Edit</button>
          </li>

        ))}
      </ul>
    </div>
  );
}

export default App;

