// import logo from './logo.svg';
import './App.css';
import {
  useEffect,
  useRef,
  useState
} from 'react';

// thêm công việc 
function App() {
  const [job, setJob] = useState([]);
  // danh sach cong viec(neu local ko luu thi tra ve mang rong)
  // toan tu nulllist
  const [jobs, setJobs] = useState([]);
  const [index, setIndex] = useState(0);
  const [indexs, setIndexs] = useState([]);
  // const refInput = useRef(null);
  // gui du lieu cua form di
  const handleSubmit = () => {
    // lấy ra mang setJobs 
    //để bảo lưu những job trong tương lai khi mà thêm job mới
    setJobs([...jobs, job])
    localStorage.setItem('jobs', JSON.stringify(jobs))
    // prev bảo lưu, job là cv mới
    // sau khi setjob thì lại trả về mảng rỗng cho input
    setJob('')
  }

  const handleEditJob = (job) => {
    let newIndexs = [...indexs, index];
    newIndexs = newIndexs.map(index => {
      return index == job[index]
    });
    setIndexs(newIndexs);
  }

  const handleDeleteJob = (value) => {
    let newJobs = [...jobs];
    newJobs = newJobs.filter(job => {
      return job !== value
    });
    setJobs(newJobs);
  };

  const handleOnKeyDown = (event) => {
    // console.log(event)
    if (event.key === 'Enter') {
      handleSubmit();
    }
  }
  const handleOnFocus = (job, index) => {
    setIndex(index)
    setJob(job)
  }
  // 
  useEffect(() => {
    const jobs = localStorage.getItem('jobs') || "[]";

    try {
      const parsedJobs = JSON.parse(jobs);
      // Array.isArray ham xac dinh kieu cuar bien co phai mang hay ko
      // ham .length de xac dinh so luong phan tu trong mang
      if (!Array.isArray(parsedJobs) || parsedJobs.length === 0) {
        return;
      }
      setJobs(parsedJobs)
    } catch (error) {
      console.log('error', error);
    }
  }, [])

  return ( <
    div className = "App" > {
      /* sd two-way binding */ } <
    input
    // ref = {refInput}
    onKeyDown = {
      handleOnKeyDown
    }
    value = {
      job
    }
    type = "text"
    // event goi setJob
    onChange = {
      e => setJob(e.target.value)
    }
    /> <
    button onClick = {
      handleSubmit
    } > ADD < /button> <
    ul > {} {
      /* render ra the li*/ } {
      jobs.map((job, index) => (
        // render job lamf children cua li
        <
        li key = {
          index
        } >
        <
        span > {
          jobs.map(index => {
            console.log(index)
          })
        } <
        /span> <
        button onClick = {
          () => handleDeleteJob(job)
        } > Delete < /button> <
        button onClick = {
          () => handleOnFocus(job, index)
        } > Edit < /button> <
        /li>

      ))
    } <
    /ul> <
    /div>
  );
}

export default App;

