  return (
    <form onSubmit={handleSubmit}>
      {props.edit ? (
        <>

        </>
      ) : (
        <>
          <input
            placeholder='Add a todo'
            value={input}
            onChange={handleChange}
            name='text'
            ref={inputRef}
          />
          <button onClick={handleSubmit}>
           Add
          </button>
        </>
      )}
    </form>
  );
}

export default TodoForm;
